// clang-format on

TEST(sha3, nullptr_256)
{
    hash256 h = sha3_256(nullptr, 0);
    EXPECT_EQ(to_hex(h), test_cases[0].expected_hash256);
}

TEST(sha3, nullptr_512)
{
    hash512 h = sha3_512(nullptr, 0);
    EXPECT_EQ(to_hex(h), test_cases[0].expected_hash512);
}

TEST(sha3, bytes)
{
    const uint8_t* const data = reinterpret_cast<const uint8_t*>(test_text);

    for (auto& t : test_cases)
    {
        const auto h256 = sha3_256(data, t.input_size);
        ASSERT_EQ(to_hex(h256), t.expected_hash256) << t.input_size;
        const auto h512 = sha3_512(data, t.input_size);
        ASSERT_EQ(to_hex(h512), t.expected_hash512) << t.input_size;
    }
}

TEST(sha3, unaligned)
{
    const auto text_length = std::strlen(test_text);
    const auto buffer_size = text_length + sizeof(uint64_t);
    std::unique_ptr<uint8_t[]> buffer(new uint8_t[buffer_size]);

    for (size_t offset = 1; offset < sizeof(uint64_t); ++offset)
    {
        uint8_t* data = &buffer[offset];
        std::memcpy(data, test_text, text_length);  // NOLINT(bugprone-not-null-terminated-result)

        for (auto& t : test_cases)
        {
            const auto h256 = sha3_256(data, t.input_size);
            ASSERT_EQ(to_hex(h256), t.expected_hash256) << t.input_size;
            const auto h512 = sha3_512(data, t.input_size);
            ASSERT_EQ(to_hex(h512), t.expected_hash512) << t.input_size;
        }
    }
}

TEST(sha3, hpp_aliases)
{
    uint8_t data[64] = {42};

    EXPECT_EQ(sha3_256_32(data).word64s[1], xhash_sha3_256_32(data).word64s[1]);
    EXPECT_EQ(sha3_512_64(data).word64s[1], xhash_sha3_512_64(data).word64s[1]);
}
